import React, { useState, useEffect } from 'react';
import Noticias from './components/Noticias';
import Comentarios from './components/Comentarios';
import Respuestas from './components/Respuestas';

function App() {
    const [noticias, setNoticias] = useState([]);
    const [comentarios, setComentarios] = useState([]);
    const [respuestas, setRespuestas] = useState([]);

    // Ejemplo de carga de noticias al montar el componente
    useEffect(() => {
        fetch('/api/notas')
            .then(response => response.json())
            .then(data => setNoticias(data))
            .catch(error => console.error('Error al cargar noticias:', error));
    }, []);

    return (
        <div className="App">
            <header>
                <h1>Sistema de Noticias</h1>
            </header>

            <main>
                {/* Componente de Noticias */}
                <Noticias noticias={noticias} setComentarios={setComentarios} />

                {/* Componente de Comentarios */}
                <Comentarios comentarios={comentarios} setRespuestas={setRespuestas} />

                {/* Componente de Respuestas */}
                <Respuestas respuestas={respuestas} />
            </main>
        </div>
    );
}

export default App;
